settings.markdownTextFunction:
var label, status, speed, fuel;
if (data && data.length) {
    label = data[0]['Label'];
    status = data[0]['status'];
    speed = data[0]['speed'];
    fuel = data[0]['fuel'];
} else {
    label = 'N/A';
    status = 'N/A';
    speed = 'N/A';
    fuel = 'N/A';
}

