    return '<div class="flex flex-row items-center justify-start">'+
           '<img class="mat-icon" src="'+ statusSvgUrl +'">' +
           '<span style="padding-left: 8px;">'+ status +'</span>' +
    '</div>';
}

function getFuelLevelHtml(fuelLevel) {
    var fuelLevelNumber = Number(fuelLevel);
    var fuelLevelSvg = getFuelLevelSvg(fuelLevelNumber);
    var encodedFuelLevelSvg = encodeURIComponent(fuelLevelSvg);
    var fuelLevelSvgUrl = 'data:image/svg+xml,' + encodedFuelLevelSvg;
